mapper.select:
select userid, username, nickname, email, token
from user
where  username=#{username} and pwd = #{password}

select userid, username, nickname, email, token
from user
where username=#{username}

select userid, username, nickname, email
from user
where userid=#{userId}

mapper.update:
update user
set username=#{username}, 
	nickname=#{nickname},
	email=#{email}
where userid=#{userid}

mapper.select:
select userid, username, nickname, email, token
from user
where username=#{username} and email=#{email}

select token
from user
where userid = #{userId}

mapper.update:
update user
set token = #{token}
where userid = #{userId}

update user
set token = #{token, jdbcType=VARCHAR}
where userid = #{userid}
