mapper.select:
select user_id, username, nickname, email, token
from user
where  username=#{username} and pwd = #{password}

select user_id, username, nickname, email, token
from user
where username=#{username}

select user_id, username, nickname, email
from user
where user_id=#{userId}

mapper.update:
update user
set username=#{username}, 
	nickname=#{nickname},
	email=#{email}
where user_id=#{user_id}

mapper.select:
select user_id, username, nickname, email, token
from user
where username=#{username} and email=#{email}

select token
from user
where user_id = #{userId}

mapper.update:
update user
set token = #{token}
where user_id = #{userId}

update user
set token = #{token, jdbcType=VARCHAR}
where user_id = #{userid}
